<?php	// Generated by curl-to-PHP: http://incarnate.github.io/curl-to-php/	$ch = curl_init();	curl_setopt($ch, CURLOPT_URL, "http://text-processing.com/api/sentiment/");	curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);	curl_setopt($ch, CURLOPT_POSTFIELDS, "text=great");	curl_setopt($ch, CURLOPT_POST, 1);	$result = curl_exec($ch);	if (curl_errno($ch)) {		echo 'Error:' . curl_error($ch);	}	//$obj = json_decode($result,true);	//var_dump($obj);		$data = json_decode( $result, true );		echo $data["label"];		echo "<pre>";	convertJSONtoArray( $data, 0 );	echo "</pre>";		function convertJSONtoArray( $data, $level )  // level - ??????? ??????????? ????? ????????? ?????? ??????????	{		foreach( $data as $key1 => $value1 )		{			if( is_array( $value1 ) )			{				echo str_repeat( "\t", $level ) . '["' . $key1 . '"] => array (' . "\n";				convertJSONtoArray( $value1, $level + 1 );				echo str_repeat( "\t", $level ) . ")," . "\n";			}			else			{				if( ( $value1=='true' ) or ( $value1=='false' ) or ( is_numeric( $value1 ) ) )  // if numeris or boolean we dont'add quotes				{					echo str_repeat( "\t", $level ) . '["' . $key1 . '"] => ' . $value1 . ',' . "\n";				}				else				{					echo str_repeat( "\t", $level ) . '["' . $key1 . '"] => "' . $value1 . '",' . "\n";				}			}		}	}		curl_close ($ch);?>